<?php
/**
 * Functions which enhance the theme by hooking into WordPress
 *
 * @package recruitfirst
 */

/**
 * Adds custom classes to the array of body classes.
 *
 * @param array $classes Classes for the body element.
 * @return array
 */
function recruitfirst_body_classes($classes)
{
    // Adds a class of hfeed to non-singular pages.
    if (!is_singular()) {
        $classes[] = 'hfeed';
    }

    if (wp_is_mobile()) {
        $classes[] = 'is-mobile';
    } else {
        $classes[] = 'is-desktop';
    }

    return $classes;
}

add_filter('body_class', 'recruitfirst_body_classes');

/**
 * Add a pingback url auto-discovery header for single posts, pages, or attachments.
 */
function recruitfirst_pingback_header()
{
    if (is_singular() && pings_open()) {
        printf('<link rel="pingback" href="%s">', esc_url(get_bloginfo('pingback_url')));
    }
    $code_head = '';
    $settings = get_option( 'rf-settings' );
    if ( isset( $settings['before_head'] ) ) {
        $code_head = $settings['before_head'];
    };
    $locale = get_locale();
    ?>
    <link rel="preconnect" href="https://fonts.gstatic.com">

    <script>
			window.home_url = '<?php echo home_url('/'); ?>';
			window.is_mobile = <?php echo json_encode(wp_is_mobile()); ?>;
			window.AJAX_URL = '<?php echo admin_url('admin-ajax.php'); ?>'
			window.CANDIDATE_URL = '<?php echo get_page_url('templates/candidate'); ?>'
			window.ajax_nonce = '<?php echo wp_create_nonce("verify_form_request"); ?>'
			window.inValidEmail = '<?php esc_html_e('Please enter a valid email', 'rf'); ?>'
			window.requiredMessage = '<?php esc_html_e('This field is required', 'rf'); ?>'
			window.phoneCodesUrl = '<?php echo THEME_URL;?>/libs/CountryCodes.json'
    </script>
    <link rel="apple-touch-icon" sizes="180x180" href="<?php echo THEME_URL; ?>/images/apple-touch-icon.png">
    <link rel="icon" type="image/png" sizes="32x32" href="<?php echo THEME_URL; ?>/images/favicon-32x32.png">
    <link rel="icon" type="image/png" sizes="16x16" href="<?php echo THEME_URL; ?>/images/favicon-16x16.png">
    <link rel="manifest" href="<?php echo THEME_URL; ?>/site.webmanifest">

    <?php if ($locale == 'zh_TW') : ?>
    <style>
        :root {
            --font-heading: 'Noto Sans TC', sans-serif;
            --font-body: 'Noto Sans TC', sans-serif;
            --font-heading-weight: 500;
        }
    </style>
    <?php elseif ($locale == 'zh_CN') : ?>
    <style>
        :root {
            --font-heading: 'Noto Sans SC', sans-serif;
            --font-body: 'Noto Sans SC', sans-serif;
            --font-heading-weight: 500;
        }
    </style>

    <?php else: ?>
        <style>
            :root {
                --font-heading: "Quicksand", Helvetica, Arial, sans-serif;
                --font-body: "Quicksand", Helvetica, Arial, sans-serif;
                --font-heading-weight: 400;
            }
        </style>
    <?php endif; ?>

    <?php
    if ($code_head) {
        echo $code_head;
    }

    if ($locale == 'zh_TW' ) {
        wp_enqueue_style( 'rf-font', 'https://fonts.googleapis.com/css2?family=Noto+Sans+TC:wght@400;500;700&display=swap', array(), _S_VERSION );
    } if ($locale == 'zh_CN') {
        wp_enqueue_style( 'rf-font', 'https://fonts.googleapis.com/css2?family=Noto+Sans+SC:wght@400;500;700&display=swap', array(), _S_VERSION );
    } else {
        wp_enqueue_style( 'rf-font', 'https://fonts.googleapis.com/css2?family=Quicksand:wght@400;500;700&display=swap', array(), _S_VERSION );
    }
}

add_action('wp_head', 'recruitfirst_pingback_header');

function recruitfirst_pingback_footer()
{
    $code_footer = '';
    $settings = get_option( 'rf-settings' );
    if ( isset( $settings['before_body'] ) ) {
        $code_footer = $settings['before_body'];
    }
    ?>
    <?php
    if ($code_footer) {
        echo $code_footer;
    }
}

add_action('wp_footer', 'recruitfirst_pingback_footer');


/**
 * Hide editor on the custom pages
 */
function hide_editor()
{
    // If not in the admin, return.
    if (!is_admin()) {
        return;
    }
    // Get the post ID on edit post with filter_input super global inspection.
    $current_post_id = filter_input(INPUT_GET, 'post', FILTER_SANITIZE_NUMBER_INT);
    // Get the post ID on update post with filter_input super global inspection.
    $update_post_id = filter_input(INPUT_POST, 'post_ID', FILTER_SANITIZE_NUMBER_INT);

    // Check to see if the post ID is set, else return.
    if (isset($current_post_id)) {
        $post_id = absint($current_post_id);
    } else if (isset($update_post_id)) {
        $post_id = absint($update_post_id);
    } else {
        return;
    }
    if (isset($post_id)) {
        $template = get_post_meta($post_id, '_wp_page_template', true);
        if ($template != '') {
            remove_post_type_support('page', 'editor');
            remove_post_type_support('page', 'comments');
        }
    }
}

add_action('init', 'hide_editor');

