/**
 * Remove prefix from archive title
 */
add_filter('get_the_archive_title', function ($title) {
    if (is_category()) {
        $title = single_cat_title('', false);
    } elseif (is_tag()) {
        $title = single_tag_title('', false);
    } elseif (is_author()) {
        $title = '<span class="vcard">' . get_the_author() . '</span>';
    } elseif (is_tax()) { //for custom post types
        $title = sprintf(__('%1$s'), single_term_title('', false));
    }
    return $title;
});

/**
 * Chage language by IP automatically
 */

add_action('template_redirect', 'rf_language_detector');
function rf_language_detector()
{
    if (!is_admin()) {
        $current_lang = pll_current_language();
        $preferred_lang = RF_Language::get_preferred_language();

        if ($current_lang != $preferred_lang) {
            $url = RF_Language::get_target_url();
            exit(wp_safe_redirect($url, 302));
        }
    }
}

function get_youtube_video_id($url)
{
    $id = '';
    preg_match('%(?:youtube(?:-nocookie)?\.com/(?:[^/]+/.+/|(?:v|e(?:mbed)?)/|.*[?&]v=)|youtu\.be/)([^"&?/ ]{11})%i', $url, $match);
    if ($match[1]) $id = $match[1];
    return $id;
}

function get_city_name($country_code)
{
    $countries_map = array(
        'HK' => __('Hong Kong'),
        'TW' => __('Taipei'),
        'ID' => __('Jakarta'),
        'MY' => __('Kuala Lumpur'),
        'CN' => __('Shanghai'),
        'SG' => __('Singapore')
    );
    return $countries_map[$country_code] ? $countries_map[$country_code] : __('Singapore');;
}

function get_country_name($country_code)
{
    $countries_map = array(
        'HK' => __('Hong Kong'),
        'TW' => __('Taiwan'),
        'ID' => __('Indonesia'),
        'MY' => __('Malaysia'),
        'CN' => __('China'),
        'SG' => __('Singapore')
    );
    return $countries_map[$country_code] ? $countries_map[$country_code] : __('Singapore');;
}

